       "avatarPath":"https://yt3.ggpht.com/ytc/AKedOLTLapu3Jygjg5gezrLXQmbZP-6MLcjtZIQ_E5qy=s88-c-k-c0x00ffffff-no-rj",
       "creatorName":"BowTiedBull",
       "views":"1044",
